
// This file serves as a fallback for the PDF.js worker
// It contains a minimal worker implementation that logs its activation
// In a production environment, you would include the full worker code

console.log("Local PDF.js worker loaded");

// Basic implementation of the PDF.js worker interface
self.onmessage = function(event) {
  console.log("PDF.js worker received message:", event.data);
  
  // Send a response back to indicate the worker is functioning
  if (event.data && event.data.cmd) {
    self.postMessage({
      cmd: event.data.cmd + 'Response', 
      success: true,
      result: 'PDF.js worker is operational'
    });
  } else {
    self.postMessage({
      error: 'Invalid command received'
    });
  }
};

// Log that worker is initialized and ready
self.postMessage({
  type: 'ready',
  message: 'Local PDF.js worker initialized'
});
